INSERT INTO public.master_data (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, data, type, "additionalValue") VALUES ('ffcdef3e-3637-41df-a90e-20b6468c6d9d', '2023-09-22 14:16:56.987546+00', '2023-10-02 09:02:21.890639+00', NULL, NULL, 'active', '{
  "imageRight": "https://plus.unsplash.com/premium_photo-1663013369685-2b00f1846c7c?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
  "description": "With the philosophy of always starting by understanding the core values of each business and organization to bring the most suitable digital solution, we are truly honored to be tied in with all well-intentioned and ambitious businesses on the journey of digital transformation",
  "imageLeft": "https://images.unsplash.com/photo-1682687982167-d7fb3ed8541d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2071&q=80",
  "imageCenter": "https://images.unsplash.com/photo-1529156069898-49953e39b3ac?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1932&q=80",
  "title": "Talent drives professionalism in Nexlab Technology"
}', 'introduction', '<p><span class="text-big"><strong>Chúng Tôi DRAGON Chemical</strong></span><br>Dragon Chemical là một công ty hàng đầu trong lĩnh vực hóa chất, chuyên cung cấp các giải pháp và sản phẩm hóa chất chất lượng cao cho khách hàng trên toàn thế giới. Với hơn một thập kỷ hoạt động, chúng tôi đã xây dựng được danh tiếng vững mạnh về sự đáng tin cậy, sáng tạo và cam kết với sự phục vụ khách hàng.</p><p><img src="https://firebasestorage.googleapis.com/v0/b/daikin-sale-staging.appspot.com/o/uploads%2F16962371630981440x1920.jpeg?alt=media&amp;token=4234e49b-7910-47c6-8eba-e02ab2a86e3c"><br><span class="text-big"><strong>Lĩnh Vực Của Chúng Tôi</strong></span></p><p style="margin-left:0px;">Chúng tôi tập trung vào việc phát triển các sản phẩm hóa chất đa dạng, từ các chất tẩy rửa và phụ gia công nghiệp đến các hợp chất phức tạp dành cho công nghiệp hóa chất và nông nghiệp. Với đội ngũ nhân viên tận tâm và giàu kinh nghiệm, Dragon Chemical cam kết cung cấp giải pháp tốt nhất cho nhu cầu cụ thể của từng khách hàng.</p><p style="margin-left:0px;"><img src="https://firebasestorage.googleapis.com/v0/b/daikin-sale-staging.appspot.com/o/uploads%2F16962371778101419x490.jpeg?alt=media&amp;token=5a07741a-1bff-4d07-8465-e78f93ddd882"><br><span class="text-big"><strong>Hoá Chất Của Chúng Tôi</strong></span></p><p style="margin-left:0px;">Chúng tôi luôn hướng đến việc phát triển bền vững và xanh hơn trong ngành công nghiệp hóa chất. Chúng tôi tuân thủ các tiêu chuẩn chất lượng cao nhất và không ngừng nghiên cứu và phát triển các sản phẩm mới để đáp ứng sự thay đổi của thị trường và nhu cầu của khách hàng.</p><p style="margin-left:0px;"><img src="https://firebasestorage.googleapis.com/v0/b/daikin-sale-staging.appspot.com/o/uploads%2F16962371902781907x735.jpeg?alt=media&amp;token=4190a8a3-d92e-4907-9230-3674ac5d6a2d"><br><span class="text-big"><strong>Trách Nhiệm Của Chúng Tôi</strong></span></p><p style="margin-left:0px;">Với tầm nhìn "Hóa chất cho một thế giới tốt hơn," Dragon Chemical cam kết góp phần vào sự phát triển bền vững và cải thiện chất lượng cuộc sống của mọi người thông qua các sản phẩm và dịch vụ chất lượng hàng đầu.<br>&nbsp;</p><figure class="table"><table><tbody><tr><td>Bảng lương</td><td>Kế toán</td><td>Giám đốc đều hành</td></tr><tr><td>Năm 2022</td><td>20.000.000 Đồng</td><td>50.000.000 Đồng</td></tr><tr><td>Năm 2023</td><td>30.000.000 Đồng</td><td>80.000.000 Đồng</td></tr></tbody></table></figure>');

INSERT INTO public.blogs (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, "typeId", code, title, description, content, banner) VALUES ('eab629a6-ba1f-4d14-9686-0f91e59503ba', '2023-09-18 15:19:08.077653+00', '2023-10-05 14:41:34.204644+00', '4b7b997c-9c2a-4c71-b8ed-31e4676f4108', NULL, 'active', '7b2b5c47-2345-41fa-b19f-a2ff19e7adcf', NULL, 'Lợi Ích Chất Thải Fukushima', 'Nước thải Fukushima là một vấn đề nghiêm trọng xuất phát từ sự cố hạt nhân Fukushima Daiichi năm 2011 ở Nhật Bản. Theo đó, khi một động đất và sóng thần cướp điện và làm hỏng hệ thống làm mát của các nhà máy điện hạt nhân, các lò phản ứng bắt đầu rò rỉ và phát ra chất phóng xạ. Để làm nguội các lò phản ứng, nước được sử dụng và sau đó biến thành nước thải chứa chất phóng xạ.', 'HTML', 'https://images.unsplash.com/photo-1620908198770-b46654453cf8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1932&q=80');
INSERT INTO public.blogs (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, "typeId", code, title, description, content, banner) VALUES ('fd20f0de-e066-4024-96b5-171c0174c741', '2023-10-05 14:43:38.885906+00', '2023-10-05 14:43:38.885906+00', '4b7b997c-9c2a-4c71-b8ed-31e4676f4108', NULL, 'active', '7b2b5c47-2345-41fa-b19f-a2ff19e7adcf', NULL, 'Nước biển quan trọng như nào', 'Nước biển là một loại nước mặn có trong các đại dương và biển trên toàn thế giới. Nó chứa một lượng lớn muối và khoáng chất, thường có màu xanh biển sâu. Nước biển quan trọng cho sự sống trên Trái Đất vì nó là môi trường sống cho nhiều loài động và thực vật biển. Nước biển cũng có tầm quan trọng lớn trong việc điều hòa khí hậu, là nguồn cung cấp thực phẩm và tạo ra một loạt các hoạt động giải trí và du lịch, như bơi biển, lặn biển, và du thuyền.', 'HTML', 'https://images.unsplash.com/photo-1559825481-12a05cc00344?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1965&q=80');
INSERT INTO public.blogs (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, "typeId", code, title, description, content, banner) VALUES ('73163d1f-c590-4d4d-9ecc-405f5a36dad3', '2023-10-05 14:44:34.11969+00', '2023-10-05 14:44:34.11969+00', '4b7b997c-9c2a-4c71-b8ed-31e4676f4108', NULL, 'active', '7b2b5c47-2345-41fa-b19f-a2ff19e7adcf', NULL, 'Cát trắng ảnh hưởng như nào', 'Cát trắng là một loại cát có màu trắng hoặc rất gần với màu trắng. Điều đặc biệt về cát trắng là sự sạch sẽ và tinh khiết của nó, với hạt cát có màu trắng sáng và mịn màng. Cát trắng thường được tạo ra từ các khoáng chất như thạch anh, thạch cao, canxi carbonat, và các loại khoáng khác', 'HTML', 'https://plus.unsplash.com/premium_photo-1666286163385-abe05f0326c4?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1975&q=80');
INSERT INTO public.blogs (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, "typeId", code, title, description, content, banner) VALUES ('32d18fae-a875-480a-a077-2115d5dabe8d', '2023-10-05 14:45:49.01555+00', '2023-10-07 05:34:42.861878+00', '4b7b997c-9c2a-4c71-b8ed-31e4676f4108', NULL, 'active', '7b2b5c47-2345-41fa-b19f-a2ff19e7adcf', NULL, 'Sứa biển tồn tại như nào', 'Sứa biển là một loài động vật biển thuộc ngành Cnidaria và họ Scyphozoa. Chúng tồn tại dưới dạng các cơ thể giữa nước biển và có một cấu trúc độc đáo có thể nhìn thấy dưới nước. ', '<h2 style="margin-left:0px;">Lorem Ipsum là gì?</h2><p style="margin-left:0px;text-align:justify;"><strong>Lorem Ipsum</strong> chỉ đơn giản là một đoạn văn bản giả, được dùng vào việc trình bày và dàn trang phục vụ cho in ấn. Lorem Ipsum đã được sử dụng như một văn bản chuẩn cho ngành công nghiệp in ấn từ những năm 1500, khi một họa sĩ vô danh ghép nhiều đoạn văn bản với nhau để tạo thành một bản mẫu văn bản. Đoạn văn bản này không những đã tồn tại năm thế kỉ, mà khi được áp dụng vào tin học văn phòng, nội dung của nó vẫn không hề bị thay đổi. Nó đã được phổ biến trong những năm 1960 nhờ việc bán những bản giấy Letraset in những đoạn Lorem Ipsum, và gần đây hơn, được sử dụng trong các ứng dụng dàn trang, như Aldus PageMaker.</p><h2 style="margin-left:0px;">Tại sao lại sử dụng nó?</h2><p style="margin-left:0px;text-align:justify;">Chúng ta vẫn biết rằng, làm việc với một đoạn văn bản dễ đọc và rõ nghĩa dễ gây rối trí và cản trở việc tập trung vào yếu tố trình bày văn bản. Lorem Ipsum có ưu điểm hơn so với đoạn văn bản chỉ gồm nội dung kiểu "Nội dung, nội dung, nội dung" là nó khiến văn bản giống thật hơn, bình thường hơn. Nhiều phần mềm thiết kế giao diện web và dàn trang ngày nay đã sử dụng Lorem Ipsum làm đoạn văn bản giả, và nếu bạn thử tìm các đoạn "Lorem ipsum" trên mạng thì sẽ khám phá ra nhiều trang web hiện vẫn đang trong quá trình xây dựng. Có nhiều phiên bản khác nhau đã xuất hiện, đôi khi do vô tình, nhiều khi do cố ý (xen thêm vào những câu hài hước hay thông tục).</p><p><br>&nbsp;</p><h2 style="margin-left:0px;">Nó đến từ đâu?</h2><p style="margin-left:0px;text-align:justify;">Trái với quan điểm chung của số đông, Lorem Ipsum không phải chỉ là một đoạn văn bản ngẫu nhiên. Người ta tìm thấy nguồn gốc của nó từ những tác phẩm văn học la-tinh cổ điển xuất hiện từ năm 45 trước Công Nguyên, nghĩa là nó đã có khoảng hơn 2000 tuổi. Một giáo sư của trường Hampden-Sydney College (bang Virginia - Mỹ) quan tâm tới một trong những từ la-tinh khó hiểu nhất, "consectetur", trích từ một đoạn của Lorem Ipsum, và đã nghiên cứu tất cả các ứng dụng của từ này trong văn học cổ điển, để từ đó tìm ra nguồn gốc không thể chối cãi của Lorem Ipsum. Thật ra, nó được tìm thấy trong các đoạn 1.10.32 và 1.10.33 của "De Finibus Bonorum et Malorum" (Đỉnh tối thượng của Cái Tốt và Cái Xấu) viết bởi Cicero vào năm 45 trước Công Nguyên. Cuốn sách này là một luận thuyết đạo lí rất phổ biến trong thời kì Phục Hưng. Dòng đầu tiên của Lorem Ipsum, "Lorem ipsum dolor sit amet..." được trích từ một câu trong đoạn thứ 1.10.32.</p><p style="margin-left:0px;text-align:justify;">Trích đoạn chuẩn của Lorem Ipsum được sử dụng từ thế kỉ thứ 16 và được tái bản sau đó cho những người quan tâm đến nó. Đoạn 1.10.32 và 1.10.33 trong cuốn "De Finibus Bonorum et Malorum" của Cicero cũng được tái bản lại theo đúng cấu trúc gốc, kèm theo phiên bản tiếng Anh được dịch bởi H. Rackham vào năm 1914.</p><h2 style="margin-left:0px;">Làm thế nào để có nó?</h2><p style="margin-left:0px;text-align:justify;">Có rất nhiều biến thể của Lorem Ipsum mà bạn có thể tìm thấy, nhưng đa số được biến đổi bằng cách thêm các yếu tố hài hước, các từ ngẫu nhiên có khi không có vẻ gì là có ý nghĩa. Nếu bạn định sử dụng một đoạn Lorem Ipsum, bạn nên kiểm tra kĩ để chắn chắn là không có gì nhạy cảm được giấu ở giữa đoạn văn bản. Tất cả các công cụ sản xuất văn bản mẫu Lorem Ipsum đều được làm theo cách lặp đi lặp lại các đoạn chữ cho tới