INSERT INTO public.categories (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, code, name, description, metadata, image, icon) VALUES ('6cffebe3-3f02-4f96-8ae6-d168cc688e59', '2023-09-18 15:19:08.085244+00', '2023-10-07 06:31:26.074619+00', NULL, NULL, 'active', 'CT001', 'Tinh Bột', 'Tinh bột là một chất tự nhiên được tìm thấy rộng rãi trong thực phẩm và cây cỏ. Đây là một dạng của carbohydrate, cấu tạo từ nhiều đường glucose liên kết với nhau thông qua các liên kết glycosidic. Tinh bột thường tồn tại dưới dạng hạt nhỏ, trong các cơ sở của cây, như hạt lúa mì, khoai tây, bắp, hoặc ngũ cốc khác.', NULL, 'https://images.unsplash.com/photo-1620065692460-d8e110a47ffb?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2080&q=80', NULL);
INSERT INTO public.categories (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, code, name, description, metadata, image, icon) VALUES ('a0df9144-8787-40fb-982c-56a461b48342', '2023-09-18 15:19:08.085244+00', '2023-10-07 06:33:40.683473+00', NULL, NULL, 'active', 'CT003', 'Chất Lỏng', 'Chất lỏng là một trạng thái của vật chất trong đó các phân tử hoặc hạt tự do có khả năng chuyển động dễ dàng và tự do trong không gian bên trong chất đó. ', NULL, 'https://images.unsplash.com/photo-1588483977150-9c2127ab7bcc?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1974&q=80', NULL);
INSERT INTO public.categories (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, code, name, description, metadata, image, icon) VALUES ('748bb4e8-ef3c-40df-aa71-57652bfdcf33', '2023-09-18 15:19:08.085244+00', '2023-10-07 06:35:23.447618+00', NULL, NULL, 'active', 'CT002', 'Hoá Chất Tổng Hợp', 'Quá trình tổng hợp hoá chất thường được thực hiện trong phòng thí nghiệm hoặc trong quy mô công nghiệp tùy thuộc vào mục đích và ứng dụng của sản phẩm. Các loại hoá chất tổng hợp có thể bao gồm các hợp chất hữu cơ (chẳng hạn như thuốc nhuộm, thuốc trừ sâu, và dược phẩm), hoặc các hợp chất vô cơ (chẳng hạn như phân bón và hợp chất khoáng).', NULL, 'https://images.unsplash.com/photo-1559598467-f8b76c8155d0?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2071&q=80', NULL);
INSERT INTO public.products (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, code, name, description, "categoryId", price, images, metadata, "htmlContent", "brandId") VALUES ('72a4409d-6175-4613-b924-b1166e7ade7d', '2023-09-18 15:19:08.085244+00', '2023-10-07 06:32:11.523226+00', NULL, NULL, 'active', 'P001', 'Hotdog', 'Hotdog là một món ăn nhanh phổ biến và ngon miệng, thường được chế biến và bán tại các quán ăn nhanh, sự kiện thể thao, và lễ hội. Một hotdog bao gồm một cái xúc xích hoặc lát mỳ, thường được nướng hoặc chiên cho đến khi nó có màu vàng và vỏ giòn.', '6cffebe3-3f02-4f96-8ae6-d168cc688e59', 50000, '["https://plus.unsplash.com/premium_photo-1683121324305-1a1deb1c5aa1?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=987&q=80"]', NULL, NULL, NULL);
INSERT INTO public.products (id, "createdAt", "updatedAt", "createdBy", "updatedBy", status, code, name, description, "categoryId", price, images, metadata, "htmlContent", "brandId") VALUES ('74b51e01-93ee-444e-8a79-34eb41a0e7d0', '2023-09-18 15:19:08.085244+00', '2023-10-07 06:32:34.572476+00', NULL, NULL, 'active', 'P002', 'Bánh mì', 'Bánh mì thường có hình dạng bánh mì trắng hoặc bánh mì ổ, có vỏ ngoài giòn và nhân bên trong mềm mịn. Bánh mì có thể được ăn kèm với nhiều loại thức ăn khác nhau như thịt, cá, rau sống, phô mai, bơ, mứt, hay mỡ pate.', '6cffebe3-3f02-4f96-8ae6-d168cc688e59', 10000, '["https://images.unsplash.com/photo-1509440159596-0249088772ff?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1172&q=80", "https://images.unsplash.com/photo-1682687981907-170c006e3744?ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2071&q=80", "https://images.unsplash.com/photo-1692911635160-1188df9e40d0?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1974&q=80", "https://plus.unsplash.com/premium_photo-1694704422627-7758067397bf?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80"]', '{}', '<h2 style="margin-left:0px;">Lorem Ipsum là gì?</h2><p style="margin-left:0px;text-align:justify;"><strong>Lorem Ipsum</strong> chỉ đơn giản là một đoạn văn bản giả, được dùng vào việc trình bày và dàn trang phục vụ cho in ấn. Lorem Ipsum đã được sử dụng như một văn bản chuẩn cho ngành công nghiệp in ấn từ những năm 1500, khi một họa sĩ vô danh ghép nhiều đoạn văn bản với nhau để tạo thành một bản mẫu văn bản. Đoạn văn bản này không những đã tồn tại năm thế kỉ, mà khi được áp dụng vào tin học văn phòng, nội dung của nó vẫn không hề bị thay đổi. Nó đã được phổ biến trong những năm 1960 nhờ việc bán những bản giấy Letraset in những đoạn Lorem Ipsum, và gần đây hơn, được sử dụng trong các ứng